
public class OperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10; // 바로 증감
		System.out.println(++a); // 11
		System.out.println(a); // 11
		
		int b=10; // 사용 후 증가
		System.out.println(b++); // 10
		System.out.println(b); // 11
	}

}
